import React, { Component } from 'react'

export class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      selectedTrack: null
    }
  }

  render() {
    const list = [
      {
        id: 1,
        title: "Campfire story"
      },
      {
        id: 2,
        title: "Booting up"
      }
    ].map(item => (
      <li
        key={item.id}
        onClick={() => this.setState({
          selectedTrack: this.title
        })}
      >
        {item.title}
      </li>
    ))
    return (
      <li>
        <h1>My little player</h1>
        <ul>{list}</ul>
        <audio src=""></audio>
      </li>
    )
  }
}

export default App
