import React, { Component } from 'react';

const campfireStory = 'https://file-examples-com.github.io/uploads/2017/11/file_example_MP3_2MG.mp3';
const bootingUp = 'https://file-examples-com.github.io/uploads/2017/11/file_example_MP3_700KB.mp3';

export class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
      selectedTrack: null,
      player: "stopped"
    }
  }
  componentDidUpdate(prevProps, prevState) {
    if (this.state.selectedTrack !== prevState.selectedTrack) {
      let track;
      switch (this.state.selectedTrack) {
        case "Campfire story":
          track = campfireStory
          break;
        case "Booting up":
          track = bootingUp
          break;
        default:
          break;
      }
      if (track) {
        this.player.src = track;
        this.player.play();
        this.setState({
          player: "playing"
        })
      }
    }
  }

  render() {
    const list = [
      {
        id: 1,
        title: "Campfire story"
      },
      {
        id: 2,
        title: "Booting up"
      }
    ].map(item => (
      <li
        key={item.id}
        onClick={() => this.setState({
          selectedTrack: item.title
        })}
      >
        {item.title}
      </li>
    ))
    return (
      <>
        <h1>My little player</h1>
        <ul>{list}</ul>
        <div>
          {
            this.state.player === "paused" &&
            <button
              onClick={() => {
                this.setState({
                  player: "playing"
                })
              }}
            >
              Play
            </button>
          }
        </div>
        <audio src="" ref={ref => this.player = ref}></audio>
      </>
    )
  }
}

export default App
